{
    "name": "Demo of HTTP Post + MQTT",
    "apiVersion": "1.0.0",
    "definitionVersion": "4.0.0",
    "type": "async",
    "description": "Demo of HTTP Post + MQTT",
    "visibility": "PUBLIC",
    "listeners": [
        {
            "type": "http",
            "cors": {
                "enabled": true,
                "allowOrigin": [
                    "*"
                ],
                "allowHeaders": [
                    "X-Gravitee-Api-Key"
                ],
                "allowMethods": [
                    "GET",
                    "POST",
                    "OPTIONS"
                ],
                "exposeHeaders": [
                    "X-Rate-Limit-Limit",
                    "X-Rate-Limit-Remaining",
                    "X-Rate-Limit-Reset"
                ]
            },
            "paths": [
                {
                    "path": "/demo/mqtt"
                }
            ],
            "entrypoints": [
                {
                    "type": "http-post"
                },
                {
                    "type": "http-get",
                    "configuration": {
                        "messagesLimitCount": 1,
                        "messagesLimitDurationMs": 10000,
                        "headersInPayload": false,
                        "metadataInPayload": false
                    }
                }
            ]
        }
    ],
    "endpointGroups": [
        {
            "name": "default",
            "type": "mqtt5",
            "endpoints": [
                {
                    "name": "HiveMQ Broker",
                    "type": "mqtt5",
                    "weight": 1,
                    "inheritConfiguration": false,
                    "configuration": {
                        "serverHost": "hivemq",
                        "serverPort": "1883",
                        "topic": "demo",
                        "consumer" : {
                            "enabled": true
                        },
                        "producer": {
                            "enabled": true
                        }
                    }
                }
            ]
        }
    ]
}

